import 'bootstrap/dist/css/bootstrap.min.css'
import { useEffect } from 'react'
import { 
    Container,
    Button,
    Form
 } from 'react-bootstrap'
import { Radio, RadioGroup} from 'react-radio-group'
import { Link, useRouteMatch } from 'react-router-dom'
import axios from "axios"
import { useState } from "react"

const EngineerTakeQuiz = () => {
    const [quiz, setQuiz] = useState([])

    const { url } = useRouteMatch()

    useEffect(() => {
        axios.get("http://127.0.0.1:5000/quiz/getQuestions").then((res) => {
          setQuiz(res.data);
        });
      }, []);

    return(
        <Container>
            <h1>All courses</h1>
            {quiz.map(quiz =>
              <Container key={quiz.question_id}>
                  <h3>{quiz.question}</h3>
                  <h5>{quiz.duration}</h5>
                  
                  {/* <Form.Check type="radio" label={quiz.option1} name="option1" />
                  <Form.Check type="radio" label={quiz.option2} name="option2"/>
                  <Form.Check type="radio" label={quiz.option3} name="option3" />
                  <Form.Check type="radio" label={quiz.option4} name="option4"/> */}
                  <RadioGroup name="fruits">
                        <Radio value="Apple" className="radio-button" />{quiz.option1}
                        <Radio value="Orange" className="radio-button" />{quiz.option2}
                        <Radio value="Banana" className="radio-button" />{quiz.option3}
                  </RadioGroup>
                  {/* if ({quiz.option3} != '') {
                    <RadioButton>{quiz.option3}</RadioButton>
                  }
                  <RadioButton>{quiz.option4}</RadioButton> */}
              </Container>
            )}
        </Container>
    )
}

export default EngineerTakeQuiz
