import 'bootstrap/dist/css/bootstrap.min.css'
import UserNavbar from "../components/UserNavbar"
import TrainerMCQQuestion from '../components/TrainerMCQQuestion'
import TrainerTFQuestion from '../components/TrainerTFQuestion'
import {
    Form,
    Container,
    Button
} from "react-bootstrap"
import { useState } from 'react'
import { useHistory } from "react-router-dom"
import axios from "axios"


const TrainerCreateQuestion = () => {

    const [form, setForm] = useState({})
    let history = useHistory()

    const setField = (field, value) => {
        setForm({
            ...form,
            [field]: value
        })
    }

    const handleSubmit = e => {
        e.preventDefault()
        axios.post("http://127.0.0.1:5000/quiz/createQuiz", form)
            .then(console.log("sent to backend!"))
        alert("quiz created! redirecting back to manage course")
        //history.push it back to the 
    }

    return (
        <div>
            <UserNavbar />
            <h1>Chapter 1 Quiz</h1>
            <Container>
                <Form>
                    <TrainerMCQQuestion />
                    <TrainerTFQuestion />
                    <Button type="submit" on>Preview Quiz</Button>
                </Form>
            </Container>
        </div>
    )
}

export default TrainerCreateQuestion
